{{FrontSide}}
<hr id=answer>
<div style='font-family: Arial; font-size: 28px;'>{{furigana:文法}}</div>
<div align="center" style='font-family: Arial; font-size: 25px;'> 
<script>
	var enters = new Array();
	var myArray = new Array();
	var conjug = new Array();
	enters ="{{接続}}".replace(/<\/div>/g,"").split("<div>");
	for(var k=0; k<enters.length; k++){ 
		document.write("<table border=\"1\">");
		myArray = enters[k].replace(/\[/g,"").replace(/\]/g,"").replace(/\・/g,"<br>").split("＋");
		conjug = myArray[0].split(" / ");
		for (var i=0; i<conjug.length; i++) {
			document.write("<tr><td align=\"right\">" + conjug[i] + "</td>");
			if(i == 0){
				for (var j=1; j<myArray.length; j++) {
					document.write("<td rowspan=\"" + conjug.length + "\"> ＋ </td>");
					document.write("<td rowspan=\"" + conjug.length + "\">" + myArray[j] + "</td>");
				}	
				document.write("</tr>");
			} else{
				document.write("</tr>");
			}
		}
		document.write("</table>");
	}
</script>
</div>
