<hr id=answer>
<div style='font-family: Arial; font-size: 32px;'>{{furigana:文法}}</div>
<div align="center" style='font-family: Arial; font-size: 25px;' id="Tabla"> 
</div>
<script>
	var enters = [];
	var myArray = [];
	var conjug = [];
	var myTable = "";
	enters = "{{接続}}".replace(/<\/div>/g,"").split("<div>");
	for (var k = 0; k < enters.length; k++) { 
		myTable += "<table>";
		myArray = enters[k].replace(/\[/g,"").replace(/\]/g,"").replace(/\・/g,"<br>").split("＋");
		conjug = myArray[0].split(" / ");
		for (var i = 0; i < conjug.length; i++) {
			myTable+="<tr><td align=\"right\">" + conjug[i] + "</td>";
			if(i == 0){
				for (var j = 1; j < myArray.length; j++) {
					myTable+= "<td rowspan=\"" + conjug.length + "\"> ＋ </td><td rowspan=\"" + conjug.length + "\">" + myArray[j] + "</td>";
				}	
			} 
			myTable+="</tr>";
		}
		myTable+="</table>";
	}
document.getElementById("Tabla").innerHTML = myTable;
</script>
